// Parse Telegram update to extract update_kind and canonical fields
// WHY Code: Telegram update has many optional top-level keys (message, edited_message, etc.)
// and nested structures. Standard nodes cannot efficiently determine which key exists.

const items = $input.all();
const results = [];

for (const item of items) {
  const update = item.json.req?.update || {};
  const _ctx = item.json._ctx || {};
  const req = item.json.req || {};

  // Determine update_kind based on which top-level key exists
  let update_kind = 'other';
  let msg = null;

  if (update.message) {
    update_kind = 'message';
    msg = update.message;
  } else if (update.edited_message) {
    update_kind = 'edited_message';
    msg = update.edited_message;
  } else if (update.channel_post) {
    update_kind = 'channel_post';
    msg = update.channel_post;
  } else if (update.edited_channel_post) {
    update_kind = 'edited_channel_post';
    msg = update.edited_channel_post;
  } else if (update.my_chat_member) {
    update_kind = 'my_chat_member';
  } else if (update.chat_member) {
    update_kind = 'chat_member';
  }

  // Extract canonical fields for message-like updates
  let parsed = {
    update_kind,
    chat_id: null,
    chat_type: null,
    message_id: null,
    message_thread_id: null,
    is_topic_message: false,
    from_user: null,
    sender_chat: null,
    text: null,
    caption: null,
    entities: null,
    caption_entities: null,
    reply_to_message_id: null,
    forward_from: null,
    forward_from_chat: null,
    forward_date: null,
    date: null,
    edit_date: null,
    via_bot: null,
    // File attachments
    document: null,
    photo: null,
    video: null,
    audio: null,
    voice: null,
    animation: null,
    sticker: null,
    // Membership
    membership_update: null
  };

  if (msg) {
    parsed.chat_id = msg.chat?.id || null;
    parsed.chat_type = msg.chat?.type || null;
    parsed.message_id = msg.message_id || null;
    parsed.message_thread_id = msg.message_thread_id || null;
    parsed.is_topic_message = msg.is_topic_message || false;
    parsed.from_user = msg.from || null;
    parsed.sender_chat = msg.sender_chat || null;
    parsed.text = msg.text || null;
    parsed.caption = msg.caption || null;
    parsed.entities = msg.entities || null;
    parsed.caption_entities = msg.caption_entities || null;
    parsed.reply_to_message_id = msg.reply_to_message?.message_id || null;
    parsed.forward_from = msg.forward_from || null;
    parsed.forward_from_chat = msg.forward_from_chat || null;
    parsed.forward_date = msg.forward_date ? new Date(msg.forward_date * 1000).toISOString() : null;
    parsed.date = msg.date ? new Date(msg.date * 1000).toISOString() : null;
    parsed.edit_date = msg.edit_date ? new Date(msg.edit_date * 1000).toISOString() : null;
    parsed.via_bot = msg.via_bot || null;
    // Files
    parsed.document = msg.document || null;
    parsed.photo = msg.photo || null; // array of PhotoSize
    parsed.video = msg.video || null;
    parsed.audio = msg.audio || null;
    parsed.voice = msg.voice || null;
    parsed.animation = msg.animation || null;
    parsed.sticker = msg.sticker || null;
    // Chat info for upsert
    parsed.chat = msg.chat || null;
  }

  // Extract membership info
  if (update_kind === 'my_chat_member' || update_kind === 'chat_member') {
    const memberUpdate = update.my_chat_member || update.chat_member;
    parsed.membership_update = memberUpdate;
    parsed.chat_id = memberUpdate?.chat?.id || null;
    parsed.chat_type = memberUpdate?.chat?.type || null;
    parsed.chat = memberUpdate?.chat || null;
  }

  // Derive visibility and dm_owner
  let visibility = 'group';
  let dm_owner_user_id = null;
  let channel = 'group';

  if (parsed.chat_type === 'private') {
    visibility = 'dm_private';
    channel = 'dm';
    // For DM, the owner is the other user (from_user)
    dm_owner_user_id = parsed.from_user?.id || null;
  }

  // Update _ctx with derived fields
  const updatedCtx = {
    ..._ctx,
    chat_id: parsed.chat_id,
    tg_user_id: parsed.from_user?.id || parsed.membership_update?.from?.id || null,
    message_id: parsed.message_id,
    channel,
    visibility,
    dm_owner_user_id
  };

  // Extract URLs from entities
  let discovered_urls = [];
  const textContent = parsed.text || parsed.caption || '';
  const entitiesToCheck = parsed.entities || parsed.caption_entities || [];

  for (const ent of entitiesToCheck) {
    if (ent.type === 'url') {
      // Extract URL from text by offset/length
      const url = textContent.substring(ent.offset, ent.offset + ent.length);
      if (url) discovered_urls.push({ type: 'url', url, entity: ent });
    } else if (ent.type === 'text_link' && ent.url) {
      discovered_urls.push({ type: 'text_link', url: ent.url, entity: ent });
    }
  }

  // Determine has_media and has_links
  const has_media = !!(parsed.document || parsed.photo || parsed.video || parsed.audio || parsed.voice || parsed.animation || parsed.sticker);
  const has_links = discovered_urls.length > 0;

  // Using a simple deterministic approach: hash of text+caption

  results.push({
    json: {
      ...item.json,
      _ctx: updatedCtx,
      _parsed: {
        ...parsed,
        visibility,
        dm_owner_user_id,
        has_media,
        has_links,
        discovered_urls,
      }
    }
  });
}

return results;

// Process file attachments and URLs for discovery
// WHY Code: Need to iterate over files and URLs, create multiple attachment records
// Standard nodes cannot handle variable number of attachments per message

const items = $input.all();
const results = [];

for (const item of items) {
  const parsed = item.json._parsed || {};
  const version_id = item.json._version_id;

  const files = [];
  const urls = parsed.discovered_urls || [];

  // Collect file attachments
  const fileTypes = ['document', 'video', 'audio', 'voice', 'animation', 'sticker'];
  for (const ft of fileTypes) {
    if (parsed[ft]) {
      files.push({
        type: ft,
        file_id: parsed[ft].file_id,
        file_unique_id: parsed[ft].file_unique_id,
        mime_type: parsed[ft].mime_type,
        file_name: parsed[ft].file_name,
        file_size: parsed[ft].file_size,
        width: parsed[ft].width,
        height: parsed[ft].height,
        duration: parsed[ft].duration,
        payload: parsed[ft]
      });
    }
  }

  // Handle photos (array of PhotoSize, take largest)
  if (parsed.photo && Array.isArray(parsed.photo) && parsed.photo.length > 0) {
    const largest = parsed.photo.reduce((a, b) => (b.file_size > a.file_size) ? b : a);
    files.push({
      type: 'photo',
      file_id: largest.file_id,
      file_unique_id: largest.file_unique_id,
      mime_type: 'image/jpeg',
      file_name: null,
      file_size: largest.file_size,
      width: largest.width,
      height: largest.height,
      duration: null,
      payload: largest
    });
  }

  results.push({
    json: {
      ...item.json,
      _attachments: {
        files,
        urls,
        files_count: files.length,
        urls_count: urls.length
      }
    }
  });
}

return results;

// Expand files into individual items for batch insert
// WHY Code: Need to split array of files into multiple items
// for Postgres batch insert into tg.files, tg.file_instances, tg.message_attachments

const items = $input.all();
const results = [];

for (const item of items) {
  const files = item.json._attachments?.files || [];
  const baseJson = item.json;

  for (const file of files) {
    results.push({
      json: {
        ...baseJson,
        _file: file
      }
    });
  }

  // If no files, still need to pass through for merge
  if (files.length === 0) {
    results.push({ json: baseJson });
  }
}

return results;

// Expand URLs into individual items for batch insert
const items = $input.all();
const results = [];

for (const item of items) {
  const urls = item.json._attachments?.urls || [];
  const baseJson = item.json;

  for (const urlObj of urls) {
    results.push({
      json: {
        ...baseJson,
        _url: urlObj.url
      }
    });
  }

  if (urls.length === 0) {
    results.push({ json: baseJson });
  }
}

return results;

