                    (() => {
                        const entities = ($json._parsed.entities || []).concat($json._parsed.caption_entities || []);
                        const text = $json._parsed.text || $json._parsed.caption || '';
                        const urls = [];
                        const seen = new Set();

                        for (const e of entities) {
                            let url = null;
                            if (e.type === 'text_link' && e.url) {
                                url = e.url;
                            } else if (e.type === 'url' && e.offset != null && e.length != null) {
                                url = text.substring(e.offset, e.offset + e.length);
                            }
                            if (url && !seen.has(url)) {
                                seen.add(url);
                                urls.push({url: url, source: 'entity'});
                            }
                        }

                        return urls;
                    })()
                }}

={{
                    (() => {
                        const files = [];
                        const p = $json._parsed;

                        // Helper to add file
                        const addFile = (type, obj) => {
                            if (!obj) return;
                            files.push({
                                type: type,
                                file_id: obj.file_id,
                                file_unique_id: obj.file_unique_id,
                                mime_type: obj.mime_type || null,
                                file_name: obj.file_name || null,
                                file_size: obj.file_size || null,
                                width: obj.width || null,
                                height: obj.height || null,
                                duration: obj.duration || null,
                                payload: obj
                            });
                        };

                        // Add each type
                        addFile('document', p.document);
                        addFile('video', p.video);
                        addFile('audio', p.audio);
                        addFile('voice', p.voice);
                        addFile('animation', p.animation);
                        addFile('sticker', p.sticker);

                        // Photo: take largest
                        if (p.photo && Array.isArray(p.photo) && p.photo.length > 0) {
                            const largest = p.photo.reduce((a, b) => (b.file_size > a.file_size ? b : a));
                            addFile('photo', {...largest, mime_type: 'image/jpeg'});
                        }

                        return files;
                    })()
                }}
