assignments.value:
=={{ (() => {
  const parts = (Array.isArray($json.extracted_text) ? $json.extracted_text : [$json.extracted_text])
    .filter(v => typeof v === "string" && v.trim() !== "");
  const joined = parts.join("\n\n");

  const metas = Array.isArray($json.extracted_meta) ? $json.extracted_meta : [$json.extracted_meta];
  const isTabular = metas.some(m => m && m.is_tabular === true);

  // кап только для табличных, чтобы не резать PDF/текстовые документы
  return isTabular ? joined.slice(0, 200000) : joined;
})() }}
