const out=[];
for (const item of $input.all()) {
  const src=item.json || {};
  const converted = src.converted_json ?? src.convertedJson ?? src.data ?? src.result ?? src.content ?? src;
  const clone=JSON.parse(JSON.stringify(converted));
  out.push({json:{
    extracted_text: typeof clone === 'string' ? clone : JSON.stringify(clone),
    extracted_meta:{extract_source:'convert_file_to_json',converted_json:clone},
    document_id: src.document_id ?? $('Carry — Pre Convert File').item.json.document_id,
    ctx: src.ctx ?? $('Carry — Pre Convert File').item.json.ctx,
    job: src.job ?? $('Carry — Pre Convert File').item.json.job
  }});
}
return out;

