const out=[];
for (const item of $input.all()) {
  const src=item.json;
  const clone=JSON.parse(JSON.stringify(src));
  out.push({json:{
    extracted_text: JSON.stringify(clone),
    extracted_meta:{extract_source:'convert_file_to_json',converted_json:clone},
    document_id: src.document_id,
    ctx: src.ctx,
    job: src.job
  }});
}
return out;

